import React from 'react'
import ItemCard from './itemCard'

const closeMarkerCSS ={
    cursor: 'pointer'
}

const ItemsCart = ({shoppingCartItems, onCartItemClick}) => (
    <div className="row">
    {(shoppingCartItems.length > 1) ?
    <div>
        {shoppingCartItems.map((item, index) =>
          <div key={index} > {(item) ? item.title : ""} {(item) ? <span style={closeMarkerCSS} onClick={() => onCartItemClick(item, index)}>X</span> : "" } </div>
        )}
    </div> : ""}
    </div>
)

export default ItemsCart;
