import { connect } from 'react-redux'
import ItemsCartList from '../components/cartItems'
import { addToCart } from '../actions';

const mapStateToProps = state => {
    console.log("state in mapStateToProps of cart element", state)
    return {
        shoppingCartItems: state.shoppingCartItems
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onCartItemClick: item => {
            console.log("about to dispatch about cart item deletion", item)
            // dispatch(addToCart(item));
        }
    }
}

const ItemsCart = connect(
    mapStateToProps,
    mapDispatchToProps
)(ItemsCartList)

export default ItemsCart
