import React from 'react'
import ItemCard from './itemCard'

const closeMarkerCSS ={
    cursor: 'pointer'
}

const ItemsCartList = ({shoppingCartItems, onCartItemClick}) => (
    <div className="row">
    <ul>
        {shoppingCartItems.map((item, index) =>
          <li key={index} > {(item) ? item.title : ""} <span style={closeMarkerCSS} onClick={() => onCartItemClick(item, index)}>X</span> </li>
        )}
    </ul>
    </div>
)

export default ItemsCartList;
