import React from 'react'
import ItemCard from './itemCard'
import Popup from 'reactjs-popup'

const PopupExample =  () => (
    <Popup trigger={<button>Trigger</button>} open={true} position="top left">
      {close => (
        <div>
          Content here
          <a className="close" onClick={close}>
            &times;
          </a>
        </div>
      )}
    </Popup>
  )

const ItemsCartList = ({shoppingCartItems, onCartItemClick}) => (
    <div className="row">
    <ul>
        {shoppingCartItems.map((item, index) =>
          <li key={index} onClick={() => onCartItemClick(item)}> {(item) ? item.title : "N/A"} </li>
        )}
    </ul>
    <PopupExample />
    </div>
)

export default ItemsCartList;
